(()=>{const t=document.querySelector(".keyboard"),e=document.querySelector("#text"),n=document.querySelector(".backspace"),c=document.querySelector(".space");t.addEventListener("click",(t=>{"BUTTON"===t.target.tagName&&(e.textContent+=t.target.textContent)})),n.addEventListener("click",(()=>{e.textContent=e.textContent.slice(0,e.textContent.length-1)})),c.addEventListener("click",(()=>{e.textContent+=" "}))})();